import { configureStore,createSlice } from "@reduxjs/toolkit";
let numReducer=createSlice(
    {
       name:'numReducer',
       initialState:{},
       reducers:{
       
       }
    }
)
export let {add}=numReducer.actions

//配置好的reducer会有以下这些属性
/**
 * actions: {add: ƒ}
caseReducers: {add: ƒ}
getInitialState: ƒ ()
name: "numReducer"
reducer: ƒ (state, action)
 */
//创建仓库，引入reducer配置

let store=configureStore({
    reducer:{
        numReducer:numReducer.reducer 
    }
})

export default store
