import { Route,Routes,Navigate } from 'react-router-dom'
import Home from '../pages/Home/index'
import Login from '../pages/Login/index'
import LayOut from '../LayOut/index'


let routes=[
    {
      path:'/',
      element:<LayOut></LayOut>,
      children:[
        {
            path:'/home',
            element:<Home></Home>,
        }
      ]
    },
    {
        path:'/login',
        element:<Login></Login>,
    },
    {
        path:'*',
        element:<div>404</div>
    }
]

//创建单个路由组件
function ItemRoute(list){
    let isToken=sessionStorage.getItem('token')
    return (
        list.map((item,index)=>{
           return <Route path={item.path} element={item.token&&!isToken?<Navigate to='/login' replace={true}></Navigate>:item.element} key={index}>
              {item.children?ItemRoute(item.children):''}
           </Route>
        })
    )
}

//创建路由表

function AddRouteList(){
    return <Routes>
        {ItemRoute(routes)}
    </Routes>
}

export default RouteList
