import {BellOutlined} from '@ant-design/icons';
import { useEffect, useState} from 'react';
import {NoticeGet} from '../../../http/api/Home'
import './index.scss'
import NoticeBox from './NoticeBox/NoticeBox';


function getNum(){
    return noticeList.forEach(element => {
            let arr=element.news.filter((item)=>{
                return item.isReaded===false
            })
            element.nums=arr.length
            nums+=element.nums
        })
    }

function HeadNotice(){
    let [noticeShow,setNoticeShow]=useState(false)
    let [noticeList,setNoitceList]=useState([])
    // let [noticeNums,setNoticeNums]=useState(11)
    useEffect(()=>{
        NoticeGet('/noticenews').then(res=>{
            setNoitceList((state)=>{
                return [
                    ...res.noticeList
                ]
            })
        })
    },[])
    //得到未读的消息条数
    let nums;
    
    getNum()
    let [noticeNums,setNoticeNums]=useState(nums)
    console.log(noticeList);
    function noticeOut(){
        setNoticeShow(noticeShow=>!noticeShow)
    }

    return (
        <div className='bellOut' >
            <BellOutlined onClick={()=>noticeOut()}/>
            {noticeNums?<div className='notice' onClick={()=>noticeOut()}>11</div>:null}
            <div id='special' className={noticeShow?'noticeShow':'normal'}>
                {noticeShow?<NoticeBox noticeList={noticeList}/>:''}
            </div>
        </div>
    )
}

export default HeadNotice
