import {BellOutlined} from '@ant-design/icons';
import { useEffect, useState} from 'react';
import {NoticeGet} from '../../../http/api/Home'
import './index.scss'

function NoticeBox(props){
    let {noticeList}=props
    //得到未读的消息条数
    noticeList.forEach(element => {
        let arr=element.news.filter((item)=>{
            return item.isReaded===false
        })
        element.nums=arr.length
    })
    //获取每个通知列表

    function changeNoticeTitle(index){
        if(index>0){
            setCenterList(state=>{
                return [
                    ...noticeList[index].news
                ]
            })
        }
        console.log(index,centerList);
    }
    return(
        <div className='NoticeBox'>
            <div className='top'>
                {
                    noticeList.map((item,index)=>{
                        return <div key={index} onClick={()=>changeNoticeTitle(index)}>{item.title}（{item.nums}）</div>
                    })
                }
            </div>
        </div>
    )
}


function HeadNotice(){
    let [noticeShow,setNoticeShow]=useState(false)
    let [noticeList,setNoitceList]=useState([])
    useEffect(()=>{
        NoticeGet('/noticenews').then(res=>{
            setNoitceList((state)=>{
                return [
                    ...res.noticeList
                ]
            })
        })
    },[])
    function noticeOut(){
        setNoticeShow(noticeShow=>!noticeShow)
    }

    return (
        <div className='bellOut' >
            <BellOutlined onClick={()=>noticeOut()}/>
            <div className='notice' onClick={()=>noticeOut()}>11</div>
            {noticeShow?<NoticeBox noticeList={noticeList}/>:''}
        </div>
    )
}

export default HeadNotice
