import {createSlice} from  "@reduxjs/toolkit";

let loginReducer=createSlice(
    {
       name:'loginReducer',
       initialState:{userInfo:{},token:sessionStorage.getItem('token')},
       reducers:{
        getUserInfo(previoustate,data){
                previoustate.userInfo=data
            },
        getUserToken(previoustate,data){
            previoustate.token=data
        }
       }
    }
)

export let {getUserInfo,getUserToken}=loginReducer.actions

export default loginReducer
