import {SearchOutlined} from '@ant-design/icons';
import { useState,useRef} from 'react';


function SearchContent(){
    let seachItems=['umi ui','Ant Design','Pro Table','Pro Layout']
    function chooseItem(item){
       console.log(item);
    }
    return (
        <div className='searchcontent'>
            {
            seachItems.map((item,index)=>{
               return <div onClick={()=>chooseItem(item)} key={index}>{item}</div>
            })
            }
        </div>
    )
}

function HomeSearch(){
    let inputRef=useRef()
    let [inputShow,setInputShow]=useState(false)
    let [inputFocus,setInputFocus]=useState(true)
    let [searchContent,setSearchContent]=useState(false)
    function seachInputOut(event){
        event.stopPropagation()
        setInputShow(true)  
    }
    function inputOnFoucs(){
      setInputFocus(inputFocus=>!inputFocus)
      if(inputFocus){
        inputRef.current.focus()
      }
    }
    function focus(){
        if(inputShow){
         setSearchContent(true)
        }
    }
    function blurs(){
        setTimeout(()=>{
            setSearchContent(false)
            setInputShow(false) 
        },100)
    }
    return (
        <div className="homesearch" onClick={()=>inputOnFoucs()}>
            <SearchOutlined onClick={(event)=>seachInputOut(event)}/>
            <input ref={inputRef} className={inputShow?'inputShow':'input'} onFocus={()=>focus()} onBlur={()=>blurs()} value={input}  placeholder='umi ui'/>
            {searchContent?<SearchContent/>:''}
        </div>
    )
}

export default HomeSearch
