import {useState} from 'react'

function NoticeBox(props){
    let {noticeList}=props
    //得到未读的消息条数
    noticeList.forEach(element => {
        let arr=element.news.filter((item)=>{
            return item.isReaded===false
        })
        element.nums=arr.length
    })
    //获取每个通知列表
    let [activeStyle,setActiveStyle]=useState(0)
    let centerList=noticeList[0].news
    function changeNoticeTitle(index){
        setActiveStyle(index)
        centerList=noticeList[index].news
        console.log(centerList);
    }

    //通知内容列表，虚拟dom形成
    function noticeVitualDom(item,index){
        if(item.isReaded){
            return <div className='box' key={index} style={{opacity:0.5}} >
                    <img src={item.img} alt=''></img>
                    <div>
                        <span>{item.content}</span>
                        <span className='time'>{item.time}</span>
                    </div>
                </div>
        }else{
            return <div className='box' key={index}>
                    <img src={item.img} alt=''></img>
                    <div>
                        <span>{item.content}</span>
                        <span className='time'>{item.time}</span>
                    </div>
                </div>
        }
    }
    //消息内容列表，虚拟dom形成
    //代办内容列表，虚拟dom形成
    return(
        <div className='NoticeBox'>
            <div className='top'>
                {
                    noticeList.map((item,index)=>{
                        return <div className={activeStyle===index?'active':''} key={index} onClick={()=>changeNoticeTitle(index)}>{item.title}（{item.nums}）</div>
                    })
                }
            </div>
            <div className='center'>
                {
                 centerList.map((item,index)=>{
                    return noticeVitualDom(item,index)
                 }) 
                }
            </div>
        </div>
    )
}

export default NoticeBox
