import {SearchOutlined} from '@ant-design/icons';
import { useState,useRef} from 'react';


function SearchContent(props){
    let seachItems=['umi ui','Ant Design','Pro Table','Pro Layout']
    function chooseItem(event,item){
        event.stopPropagation()
        props.sonInfo(item)
        props.inputShow=false
    }
    return (
        <div className='searchcontent'>
            {
            seachItems.map((item,index)=>{
               return <div onClick={(event)=>chooseItem(event,item)} key={index}>{item}</div>
            })
            }
        </div>
    )
}

function HomeSearch(){
    let inputRef=useRef()
    let [inputShow,setInputShow]=useState(false)
    let [inputFocus,setInputFocus]=useState(true)
    let [searchContent,setSearchContent]=useState(false)
    let [inputContent,setInputContent]=useState('umi ui')
    function seachInputOut(event){
        event.stopPropagation()
        setInputShow(true)  
    }
    function inputOnFoucs(){
      setInputFocus(inputFocus=>!inputFocus)
      if(inputFocus){
        inputRef.current.focus()
      }
      
    }
    function focus(e){
        if(inputShow){
         setSearchContent(true)
        }
        console.log(e.target);
    }
    function sonInfo(value){
        console.log(value);
        setInputContent(value)
        inputRef.current.focus()
       
    }
    return (
        <div className="homesearch" onClick={()=>inputOnFoucs()}>
            <SearchOutlined onClick={(event)=>seachInputOut(event)}/>
            <input ref={inputRef} className={inputShow?'inputShow':'input'} onFocus={(e)=>focus(e)} value={inputContent} placeholder='站内搜索'/>
            {searchContent?<SearchContent inputShow={inputShow} sonInfo={sonInfo}/>:''}
        </div>
    )
}

export default HomeSearch
