import {SearchOutlined} from '@ant-design/icons';
import { useState,useRef} from 'react';




function HomeSearch(){
    let inputRef=useRef()
    let [inputShow,setInputShow]=useState(false)
    function seachInputOut(event){
        event.stopPropagation()
        setInputShow(true)  
    }
    function inputOnFoucs(){
      console.log(inputRef);
      
    }
    return (
        <div className="homesearch" onClick={()=>inputOnFoucs()}>
            <SearchOutlined onClick={(event)=>seachInputOut(event)}/>
            <input ref={inputRef} className={inputShow?'inputShow':'input'}></input>
        </div>
    )
}

export default HomeSearch
