import { Navigate, Route,Routes} from 'react-router-dom'
import { useSelector } from "react-redux";
import Login from '../pages/Login/index'
import LayOut from '../LayOut/index'


let routes=[
    {
      path:'/',
      token:true,
      element:<LayOut></LayOut>,
    //   children:[
    //     {
    //         path:'/home',
    //         token:true,
    //         element:<Home></Home>,
    //     }
    //   ]
    },
    {
        path:'/login',
        element:<Login></Login>,
    },
    {
        path:'*',
        element:<div>404</div>
    }
]

//创建单个路由组件
function ItemRoute(list,isToken){
    return (
        list.map((item,index)=>{
           return <Route path={item.path} element={item.token&&!isToken?<Navigate to='./login'></Navigate>:item.element} key={index}>
              {item.children?ItemRoute(item.children):''}
           </Route>
        })
    )
}

//创建路由表

function AddRouteList(){
    let nav=useSelector(state=>{

    })
    let isToken=useSelector(state=>{return state.loginReducer.token})
    return <Routes>
        {ItemRoute(routes,isToken)}
    </Routes>
}

export default AddRouteList
